function nextStep(t){document.getElementById("step"+t).classList.remove("active"),document.getElementById("step"+(t+1)).classList.add("active")}function prevStep(t){document.getElementById("step"+t).classList.remove("active"),document.getElementById("step"+(t-1)).classList.add("active")}document.getElementById("multiForm").addEventListener("submit",(function(t){t.preventDefault();const e=t.target,n=new FormData(e);fetch(e.action,{method:"POST",body:n}).then((t=>{t.ok?window.location.href="/thank-you.html":alert("Something went wrong. Please try again.")})).catch((()=>{alert("Something went wrong. Please try again.")}))}));