const lightbox=document.getElementById("lightbox"),lightboxImg=document.getElementById("lightbox-img");function closeLightbox(){lightbox.style.display="none",lightboxImg.src=""}document.querySelectorAll(".lightbox-thumb").forEach((t=>{t.addEventListener("click",(()=>{lightboxImg.src=t.dataset.full,lightbox.style.display="flex"}))})),lightbox.addEventListener("click",closeLightbox);